parameters.jsCode:
// Access the JSON string from the correct path
const resultJson = $input.first().json.data.resultJson; 

// Check if resultJson is a string and parse it if needed
let parsedJson;
if (typeof resultJson === 'string') {
    parsedJson = JSON.parse(resultJson); // Parse the JSON string to an object
} else {
    parsedJson = resultJson; // If it's already an object, no need to parse
}

// Extract the URL from the resultUrls array
const url = parsedJson.resultUrls[0]; // Get the first URL from the array

// Return the URL wrapped in an object within an array as required by n8n
return [{ json: { extractedUrl: url } }];
